<?phpuse yii\grid\GridView;use yii\helpers\Html;$this->title = 'เลือแบบสอบถาม';$this->params['breadcrumbs'][] = ['label' => 'Customers', 'url' => ['index']];\yii\widgets\Pjax::begin();echo GridView::widget([    'dataProvider' => $dataProviderSurvey,    'filterModel' => $searchModelSurvey,    'columns' => [        ['class' => 'yii\grid\SerialColumn'],        [            'attribute' => 'site_id',            'filter' => Html::activeDropDownList($searchModelSurvey, 'site_id', $searchModelSurvey->siteItems, [                'class' => 'form-control',                'prompt' => '--เลือกไซต์งาน/โครงการ--'            ]),            'value' => 'site.site_name'        ],        'name',        [            'class' => 'yii\grid\ActionColumn',            'header' => 'Actions',            'template' => '{do}',            'buttons' => [                'do' => function ($url, $model) {                    return Html::a('<span class="fa fa-edit"></span> กรอกแบบสอบถาม', $url, [                        'title' => Yii::t('app', 'View'),                        'class' => 'btn btn-primary btn-xs',                    ]);                }            ],            'urlCreator' => function ($action, $model, $key, $index) {                if ($action === 'do') {                    $customerId = Yii::$app->request->get('id');                    $url = \yii\helpers\Url::to(['survey/do', 'id' => $model->id, 'customer_id' => $customerId]);                    return $url;                }            }        ],    ],]);\yii\widgets\Pjax::end();?>