          'tite_id' => $k_title,                        'item' => $v_type['question'],                    ]);                    $questionSeq++;                }            }            if (!empty($v_title['items'])) {                $questionSeq = 1;                foreach ($v_title['items'] as $k_question => $v_question) {                    echo'<div class="col-xs-12 col-sm-6 col-md-6">';                    if (                        $v_question['type_id'] == \backend\modules\crm\models\Question::TYPE_RADIO ||                        $v_question['type_id'] == \backend\modules\crm\models\Question::TYPE_CHECKBOX                      ) {                        $text .= $this->render('/survey/choice/type_choice', [                            'questionSeq' => $questionSeq,                            'survey_id' => $survey_id,                            'tab_id' => $k_tab,                            'tite_id' => $k_title,                            'modelResponse' => $modelResponse,                            'modelQuestion' => $v_question['modelQuestion'],                            'answerSingle' => $answerSingle,                            'answerMultiple' => $answerMultiple,                            'answerText' => $answerText,                            'responseId' => $modelResponse->id,                        ]);                    } else {                        $text .= $this->render('/survey/choice/type_choice_single', [                            'survey_id' => $survey_id,                            'tab_id' => $k_tab,                            'tite_id' => $k_title,                            'questionSeq' => $questionSeq,                            'modelResponse' => $modelResponse,                            'modelQuestion' => $v_question['modelQuestion'],                            'answerSingle' => $answerSingle,                            'answerMultiple' => $answerMultiple,                            'answerText' => $answerText,                            'responseId' => $modelResponse->id,                        ]);                    }                    echo'</div>';                    $questionSeq++;                }            }        }        $items_d['content'] = $text;        $items_x[] = $items_d;    }}$form = ActiveForm::begin([    'type' => ActiveForm::TYPE_HORIZONTAL,    'id' => 'form-survey',]);echo TabsX::widget([    'items' => $items_x,    'position' => TabsX::POS_ABOVE,    'encodeLabels' => false]);echo Html::hiddenInput('survey_id', $survey_id);echo Html::hiddenInput('customer_id', $customer_id);echo Html::submitButton("บันทึก", ['id' => 'btn-save-survey', 'class' => 'btn btn-primary']);ActiveForm::end();?>    <!--<hr>-->    <!--<div class="input-group">-->    <!--    <input id="slider1" type="range" min="0" max="100" step="10" class="" />-->    <!--    <input id="rangeShow" type="text" size="2" class="form-control">-->    <!--</div>-->    <!--Meter : <meter min="0" max="100" value="0" id="show1">95 of 100</meter>-->    <!--<br>-->    <!--Progress of Task A : <progress value="0" max="100" id="show2">60%</progress>-->    <!--<br>--><?phpModal::begin([    'header' => '<h2></h2>',    'id' => 'modall-view',    // 'toggleButton' => ['label' => 'click me'],]);echo 'Say hello...';Modal::end();