<?phpuse yii\helpers\Html;use backend\modules\qtn\models\Question;?><?phpecho '<div class="row">';if ($modelQuestion->type_id == '1') {    echo '<h4> ' . $modelQuestion->seq . ') ' . $modelQuestion->name . '</h4>';    echo Html::radio("radio[{$modelQuestion->id}]", false, ['value' => 'Y']);    echo Html::label("Yes");    echo Html::radio("radio[{$modelQuestion->id}]", false, ['value' => 'N']);    echo Html::label("No");} else {    echo '<h4> ' . $modelQuestion->seq . ') ' . $modelQuestion->name . '</h4>';    echo html::beginTag('p');    switch ($modelQuestion->type_id) {        case Question::TYPE_TEXT : // Input single line            $answer = isset($answerText[$modelQuestion->id])?$answerText[$modelQuestion->id]:'';            echo Html::textInput("text[{$modelQuestion->id}]", $answer, ['class' => 'form-control']);            break;        case Question::TYPE_TEXTAREA  : // Text multiline            $answer = isset($answerText[$modelQuestion->id])?$answerText[$modelQuestion->id]:'';            echo Html::textarea("text[{$modelQuestion->id}]", $answer, ['class' => 'form-control', 'rows' => '5']);            break;        case Question::TYPE_NUMBER : // number            $answer = isset($answerText[$modelQuestion->id])?$answerText[$modelQuestion->id]:'';            echo Html::beginTag('div', ['class' => 'col-xs-12 col-sm-2 col-md-2']);            echo Html::input('text',"text[{$modelQuestion->id}]",$answer,['class'=>'form-control']);            echo Html::endTag('div');            break;        case Question::TYPE_DATE : // Date            echo \kartik\widgets\DatePicker::widget([                'name' => "input[{$modelQuestion->id}]",                //'addon' => ['append' => ['content'=>' คน']],            ]);            break;    }    echo Html::endTag('pre');    echo'</div>';}?>