<?phpuse yii\helpers\Html;use yii\widgets\DetailView;use kartik\tabs\TabsX;use kartik\form\ActiveForm;use yii\bootstrap\Modal;use backend\modules\crm\SurveyAsset;SurveyAsset::register($this);/* @var $this yii\web\View *//* @var $model backend\modules\crm\models\Response */$this->title = $model->name;$this->params['breadcrumbs'][] = ['label' => 'Responses', 'url' => ['index']];$this->params['breadcrumbs'][] = $this->title;?><?php$items_d = [];$items_x = [];$question_c = $models;if (count($question_c) > 0) {    foreach ($question_c as $k_tab => $v_tab) {        $text = '';        foreach ($v_tab['item'] as $k_title => $v_title) {            if($v_title['title_hide'] == 0 ){                $text .= '<div class="row"><h4> ' . $v_title['title_name']. '</h4></div>';            }            if (!empty($v_title['item'])) {                $questionSeq = 1;                foreach ($v_title['item'] as $k_type => $v_type) {                    $text .= $this->render('choice/type_table_multi', [                        'survey_id' => $survey_id,                        'questionSeq' => $questionSeq,                        'tab_id' => $k_tab,                        'tite_id' => $k_title,                        'item' => $v_type['question'],                    ]);                    $questionSeq++;                }            }            if (!empty($v_title['items'])) {                $questionSeq = 1;                foreach ($v_title['items'] as $k_question => $v_question) {                    if ($v_question['type_id'] == '4' || $v_question['type_id'] == '5') {                        $text .= $this->render('/survey/choice/type_choice', [                            'questionSeq' => $questionSeq,                            'survey_id' => $survey_id,                            'tab_id' => $k_tab,                            'tite_id' => $k_title,                            'modelResponse' => $modelResponse,                            'modelQuestion' => $v_question['modelQuestion'],                            'answerSingle' => $answerSingle,                            'answerMultiple' => $answerMultiple,                            'answerText' => $answerText,                            'responseId' => $modelResponse->id,                        ]);                    } else {                        $text .= $this->render('/survey/choice/type_choice_single', [                            'survey_id' => $survey_id,                            'tab_id' => $k_tab,                            'tite_id' => $k_title,                            'questionSeq' => $questionSeq,                            'modelQuestion' => $v_question['modelQuestion'],                            'answerSingle' => $answerSingle,                            'answerMultiple' => $answerMultiple,                            'answerText' => $answerText,                             'responseId' => $modelResponse->id,                        ]);                    }                    $questionSeq++;                }            }        }        $items_d['label'] = $v_tab['tab_name'];        $items_d['content'] = $text;        $items_x[] = $items_d;    }}$form = ActiveForm::begin([    'type' => ActiveForm::TYPE_HORIZONTAL,    'id' => 'form-survey',]);echo TabsX::widget([    'items' => $items_x,    'position' => TabsX::POS_ABOVE,    'encodeLabels' => false]);echo Html::hiddenInput('survey_id', $survey_id);echo Html::hiddenInput('customer_id', $customer_id);ActiveForm::end();?>